(()=>{"use strict";class t{constructor(t,e){this._name=t,this._type=e,this.health=100,this.level=1}get type(){if(!0===t.types.includes(this._type))return this._type;throw new Error("Недопустимый тип персонажа")}get name(){if(1==(this._name.length<2||this._name.length>10))throw new Error("Недопустимая длина имени");return this._name}levelUp(){if(0===this.health)throw new Error("Нельзя повысить левел умершего");this.level+=1,this.attack+=.2*this.attack,this.defence+=.2*this.defence,this.health=100}damage(t){this.health>=0&&(this.health-=t*(1-this.defence/100))}}t.types=["Bowman","Swordsman","Magician","Daemon","Undead","Zombie"]})();